mapper.select:
SELECT user_name FROM sys_user WHERE id IN ( SELECT user_id FROM user_role WHERE role_id IN ( SELECT id FROM roles WHERE nick_name IN
#{roleName}
) GROUP BY user_id HAVING COUNT(DISTINCT role_id) =
1
)

SELECT r.mname FROM roles r JOIN ( SELECT rp.role_id FROM role_permission rp WHERE rp.permission_id IN
#{permissionId}
GROUP BY rp.role_id HAVING COUNT(DISTINCT rp.permission_id) =
${clue.size()}
) sub ON r.id = sub.role_id
